mapper.update:
update member 
set	name = #{name}, age = #{age}, 
	gender = #{gender}, email = #{email}
where id = #{id}

mapper.select:
select * 
from   (select b.*, rownum rnum
		from (select * 
			  from member 
			  where id != 'admin'

						and

							id
							name
							age
							gender

						like #{search_word}

			  order by id
			  )b
		where rownum <= #{end}
	)
where rnum >= #{start} and rnum <= #{end}
